import React, { Component } from 'react';
import './App.css';
import Todo from './Todo';
import NewTodo from './NewTodo';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
        todos: [
          // {
          //   "id":"0f330e00-35f2-11eb-822f-5bc0d6be0940",
          //   "text": "Homework",
          //   "user": "e78d52-d76aec-34ec77-1f346d-940c93",
          //   "completed": true,
          //   "created_at": "2020-12-04 05:32:11",
          //   "updated_at": "2020-12-04 05:39:06"
          // },

          // {
          //   "id":"0f330e00-35f2-11eb-822f-5bc0d6be0940",
          //   "text": "Drink Tea",
          //   "user": "e78d52-d76aec-34ec77-1f346d-940c93",
          //   "completed": true,
          //   "created_at": "2020-12-04 05:32:11",
          //   "updated_at": "2020-12-04 05:39:06"
          // },
        ], input: ''

    };
    this.onChange =this.onChange.bind(this);
    this.addTodo = this.addTodo.bind(this);

  }

  // this.setState({
  //   todos: [...this.state.todos, newTodo]
  // )};



  onChange(event) {
    this.setState({
      input: event.target.value
    });
  }

  addTodo(event) {
    event.preventDefault();
    var self = this;
    // const newTodo = this.state.input;
    var data = {text: this.state.input};

    console.log(this.state.input);
    var createRequest = new XMLHttpRequest();

// Response handler
createRequest.onreadystatechange = function() {

    // Wait for readyState = 4 & 200 response
    if (this.readyState === 4 && this.status === 200) {

        // parse JSON response
        self.setState({
          todos: [...self.state.todos, JSON.parse(this.responseText)]
        });

        // var todo = JSON.parse(this.responseText);
        // this.setState({...this.state.todos, newTodo});
        // this.state.input = '';
        // self.setState({input:''});
        // console.log(todo);

    } else if (this.readyState === 4) {

        // this.status !== 200, error from server
        console.log(this.responseText);

    }
};

createRequest.open("POST", "https://cse204.work/todos", true);
createRequest.setRequestHeader("Content-type", "application/json");
createRequest.setRequestHeader("x-api-key", "e78d52-d76aec-34ec77-1f346d-940c93");
createRequest.send(JSON.stringify(data));
}

componentDidMount() {
  //Make initial AJAX call to list todos

  const self = this;
  var xhttp = new XMLHttpRequest();

xhttp.onreadystatechange = function() {
  if (this.readyState === 4 && this.status === 200) {
      var todos = JSON.parse(xhttp.responseText);
      self.setState({todos: todos});

      console.log(todos);
}
};

xhttp.open("GET", "https://cse204.work/todos", true);
xhttp.setRequestHeader("x-api-key","e78d52-d76aec-34ec77-1f346d-940c93");
xhttp.send();

}


  render() {


    return (

<div className = "App">
<h1> Samantha's To-Do List: </h1>
<section id="myTodos">
<NewTodo addTodo={this.addTodo} newTodo={this.newTodo} onChange={this.onChange} input={this.state.input} />

{/* <Todo id="12345" text="text" />
<Todo id="4567" text="test test" /> */}
{
  this.state.todos.map((todo) =>
    <Todo key={todo.id} id={todo.id} text={todo.text} />
     )
}
      </section>

</div>

    );
  }
}

export default App;
