import React, { Component } from 'react';
import './App.css';
import Todo from './Todo';
import NewTodo from './NewTodo';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
        todos: [
          {
            "id":"0f330e00-35f2-11eb-822f-5bc0d6be0940",
            "text": "Homework",
            "user": "e78d52-d76aec-34ec77-1f346d-940c93",
            "completed": true,
            "created_at": "2020-12-04 05:32:11",
            "updated_at": "2020-12-04 05:39:06"
          },

          {
            "id":"0f330e00-35f2-11eb-822f-5bc0d6be0940",
            "text": "Drink Tea",
            "user": "e78d52-d76aec-34ec77-1f346d-940c93",
            "completed": true,
            "created_at": "2020-12-04 05:32:11",
            "updated_at": "2020-12-04 05:39:06"
          },
        ]

    };
  }


  componentDidMount() {
    //Make initial AJAX call to list todos
    var xhttp = new XMLHttpRequest();

xhttp.onreadystatechange = function() {
    if (this.readyState == 4 && this.status == 200) {
        var todos = JSON.parse(this.responseText);



        for (i=0; i < todos.length; i++) {
            var li = document.createElement("li");
        var btn = document.createElement("button");
        li.innerHTML=todos[i].text;

        var check = document.createElement("input");
        check.setAttribute("type", "checkbox");
        check.setAttribute("id", "checked");
        check.addEventListener('click', checkList);

        li.setAttribute("id", todos[i].id);
        //checkbox appended to each list
        li.appendChild(check);

        //delete button appended to each list
        btn.innerHTML = "DELETE";
        btn.addEventListener('click', deleteTodo);
        li.appendChild(btn);


        document.getElementById("list").appendChild(li);


        }

        console.log(todos);
}
};


xhttp.open("GET", "https://cse204.work/todos", true);
xhttp.setRequestHeader("x-api-key","e78d52-d76aec-34ec77-1f346d-940c93");
xhttp.send();

    // this.state.todos = 
  }


  render() {
    return (

<div className = "App">
<h1> Samantha's To-Do List: </h1>
<section id="myTodos">
<NewTodo />

<Todo id="12345" text="text" />
<Todo id="4567" text="test test" />
{
  this.state.todos.map((todo) =>
    <Todo key={todo.id} id={todo.id} text={todo.text} />
     )
}
      </section>

</div>

    );
  }
}

export default App;
